import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchProfiles, setCurrentPage, sortProfiles } from '../../features/userSlice';
import ProfileCard from '../ProfileCard';
import Popup from 'reactjs-popup';
import { FaPlus, FaFilter, FaTimes } from 'react-icons/fa';
import AddProfileForm from '../AddProfileForm';
import './index.css';

const Home = () => {
    const dispatch = useDispatch();
    const profiles = useSelector((state) => state.users.profiles) || []; // Default to empty array
    const status = useSelector((state) => state.users.status);
    const currentPage = useSelector((state) => state.users.currentPage);
    const itemsPerPage = 4; // Assuming a constant number of items per page

    useEffect(() => {
        dispatch(fetchProfiles(currentPage));
    }, [dispatch, currentPage]);

    if (status === 'loading') {
        return <div>Loading...</div>;
    }

    if (status === 'failed') {
        return <div>Error loading profiles</div>;
    }

    // Handle page change
    const handlePageChange = (page) => {
        dispatch(setCurrentPage(page));
        dispatch(fetchProfiles(page)); // Fetch new page data
    };

    // Calculate the profiles to display
    const startIndex = (currentPage - 1) * itemsPerPage;
    const displayedProfiles = profiles.slice(startIndex, startIndex + itemsPerPage);

    return (
        <div className="home-container">
            <div className="header">
                <Popup
                    trigger={<FaPlus className="icon" />}
                    modal
                    nested
                    contentStyle={{ padding: '0', borderRadius: '10px', background: '#fff' }}
                >
                    {(close) => (
                        <div className="popup-card">
                            <div className="popup-header">
                                <h3>Create New User</h3>
                                <button className="close-button" onClick={close}>
                                    <FaTimes />
                                </button>
                            </div>
                            <AddProfileForm closePopup={close} />
                        </div>
                    )}
                </Popup>
                <button className="sort-button" onClick={() => dispatch(sortProfiles())}>
                    <FaFilter className="icon" />
                    Sort by Location
                </button>
            </div>
            <div className="profiles-grid">
                {displayedProfiles.length > 0 ? (
                    displayedProfiles.map((profile) => (
                        <ProfileCard key={profile._id} profile={profile} />
                    ))
                ) : (
                    <div>No profiles available</div>
                )}
            </div>
            <div className="pagination">
                {Array.from({ length: Math.ceil(profiles.length / itemsPerPage) }).map((_, index) => (
                    <button
                        key={index}
                        className={`page-button ${currentPage === index + 1 ? 'active' : ''}`}
                        onClick={() => handlePageChange(index + 1)}
                    >
                        {index + 1}
                    </button>
                ))}
            </div>
        </div>
    );
};

export default Home;
